import React, { Component } from 'react';

import './Board.css';

class Board extends Component {
    constructor(props) {
        super(props);
        this.state={
            gameBoard: Array(100).fill(null),
            boxColor: ['blue', 'red', 'green'],
            ships: [0, 1, 2, 3, 22],
            torpedoCount: 10,
            hitCount: 0,
            statusMessage: ''
        }
    }

    statusMessage = (message) => {
        this.setState({statusMessage: message})
    }

    checkWinner =()=> {
        if(this.state.hitCount == this.state.ships.length-1) {
            this.statusMessage('You\'ve won!')
        }
    }

    displayMissed = (ships) => {
        for (let i = 0; i < ships.length; i++) {
            if (this.state.gameBoard[ships[i]] === "null") {
                let missed = this.state.gameBoard
                missed[ships[i]] = "m"
                this.setState({
                    gameBoard: missed,
                })
            }
        }
    }


    playerClick = (i) => {
        let {ships, torpedoCount, hitCount} = this.state
        if (torpedoCount <= 0) {
            this.statusMessage("You're out of torpedos!")
            displayMissed(this.state.ships)

        } else {
            if (this.state.ships.includes(i)) {
                let hit = this.state.gameBoard
                hit[i] = "x"
                this.setState({
                    gameBoard: hit,
                    torpedoCount: torpedoCount -1,
                    hitCount: hitCount +1
                })
            } else {
                let miss = this.state.gameBoard
                miss[i] = "o"
                this.setState({
                    gameBoard: miss,
                    torpedoCount: torpedoCount -1
                })
            }
        }
            console.log(hitCount);
            this.checkWinner()
    }

    displayColor = (i) => {
        if (this.state.gameBoard[i]==='x') {
            return this.state.boxColor[1]
        } else if (this.state.gameBoard[i]==='o') {
                return this.state.boxColor[2]
        } else {
            return this.state.boxColor[0]
        }
    }

