import React, { Component } from 'react';

import './Board.css';

class Board extends Component {
    constructor(props) {
        super(props);
        this.state={
            gameBoard: Array(100).fill(null),
            boxColor: ['blue', 'red', 'green'],
            ships: [0, 1, 2, 3, 22],
            torpedoCount: 10
        }
    }

    playerClick = (i) => {
        let {torpedoCount} = this.state
        if (torpedoCount == 0) {
            console.log("You're out of torpedos!")
        }
        if (this.state.ships.includes(i)) {
            let hit = this.state.gameBoard
            hit[i] = "x"
            this.setState({
                gameBoard: hit,
                torpedoCount: torpedoCount -1
            })
        } else {
            let miss = this.state.gameBoard
            miss[i] = "o"
            this.setState({
                gameBoard: miss,
                torpedoCount: torpedoCount -1
            })

        }
    }

    displayColor = (i) => {
        if (this.state.gameBoard[i]==='x') {
            return this.state.boxColor[1]
        } else if (this.state.gameBoard[i]==='o') {
                return this.state.boxColor[2]
        } else {
            return this.state.boxColor[0]
        }
    }

