  render() {
    return (
        <div className="boardContainer">
            <div className="board">
            {this.state.gameBoard.map((el, i) => (
                    <div onClick={() => this.playerClick(i)} style={{backgroundColor: this.displayColor(i)}} className="box i" id={i}>
                    {i}
                    </div>
            ))}
            </div>
            <p>{this.state.torpedoCount}</p>
        </div>
    );
  }
}

export default Board;
