import React, { Component } from 'react';

import './Board.css';

class Board extends Component {
    constructor(props) {
        super(props);
        this.state={
            gameBoard: Array(100).fill(null),
            boxColor: ['blue', 'red'],
            Ships: [0, 1, 2, 3, 4]
        }
}




  render() {
    return (
        <div className="boardContainer">
            <div className="board">
                {this.state.gameBoard.map((el,i) => (
                <div style={{backgroundColor: this.state.boxColor[1]}} className="box i" id={i}>{i}</div>
                ))}
            </div>
        </div>
    );
  }
}

export default Board;
