import React, { Component } from 'react';

import './Board.css';

class Board extends Component {
    constructor(props) {
        super(props);
        this.state={
            gameBoard: Array(100).fill(null),
            boxColor: ['blue', 'red', 'green', 'yellow'],
            ships: [],
            torpedoCount: 10,
            hitCount: 0,
            statusMessage: ''
        }
    }

    statusMessage = (message) => {
        this.setState({statusMessage: message})
    }

    checkWinner =()=> {
        if(this.state.hitCount == this.state.ships.length-1) {
            this.statusMessage('You\'ve won!')
        }
    }

    displayMissed = (ships) => {
        let message = "You did not use your torpedos wisely, young padawan"
        for (let i = 0; i < ships.length; i++) {
            if (this.state.gameBoard[ships[i]] === null) {
                let missed = this.state.gameBoard
                missed[ships[i]] = "m"
                this.setState({
                    gameBoard: missed,
                    statusMessage: message
                })
            }
        }return message
    }


    playerClick = (i) => {
        let {ships, gameBoard, torpedoCount, hitCount} = this.state
        if (gameBoard[i] == null && hitCount < ships.length) {
            if (torpedoCount <= 0) {
                this.statusMessage("You're out of torpedos!")
                this.displayMissed(ships)

            } else {
                if (ships.includes(i)) {
                    let hit = gameBoard
                    hit[i] = "x"
                    this.setState({
                        gameBoard: hit,
                        torpedoCount: torpedoCount -1,
                        hitCount: hitCount +1
                    })
                } else {
                    let miss = gameBoard
                    miss[i] = "o"
                    this.setState({
                        gameBoard: miss,
                        torpedoCount: torpedoCount -1
                    })
                }
            }
            console.log(hitCount);
            this.checkWinner()
        }
    }

    displayColor = (i) => {
        if (this.state.gameBoard[i]==='x') {
            return this.state.boxColor[1]
        } else if (this.state.gameBoard[i]==='o') {
            return this.state.boxColor[2]
        } else if (this.state.gameBoard[i]==='m') {
            return this.state.boxColor[3]
        } else {
            return this.state.boxColor[0]
        }
    }

    shipYard = () => {
         return Math.floor(Math.random() * 100)
    }

    checkShip = () => {
        let takenIndex = []
        let tempShips = []
        // let indexNums = [-11, -10, -9, -1, 0, 1, 9, 10, 11]

        for (let i = 0; tempShips.length < 5; i++) {
            let tempShip = this.shipYard()
            if (takenIndex.includes(tempShip)) {
            } else {
                tempShips.push(tempShip)
            }
        }
        this.setState({
            ships: tempShips,
            gameBoard: Array(100).fill(null),
            boxColor: ['blue', 'red', 'green', 'yellow'],
            torpedoCount: 10,
            hitCount: 0,
            statusMessage: ''
        })
    }

  render() {
      console.log(this.shipYard())
      console.log(this.state.ships)
    return (
        <div className="boardContainer">
            <div className="board">
            {this.state.gameBoard.map((el, i) => (
                    <div onClick={() => this.playerClick(i)} style={{backgroundColor: this.displayColor(i)}} className="box i" id={i}>
                    {i}
                    </div>
            ))}
            </div>
            <p>{this.state.torpedoCount} Status: {(this.state.torpedoCount === 0) ? this.displayMissed(this.state.ships) : this.state.statusMessage}</p>
            <button onClick={this.checkShip}>Start GGenerate Ships</button>
        </div>
    );
  }
}

export default Board;
