import React, { Component } from 'react';

import './Board.css';

class Board extends Component {
    constructor(props) {
        super(props);
        this.state={
            gameBoard: Array(100).fill(null),
            boxColor: ['blue', 'red'],
            ships: [0, 1, 2, 3, 4],
        }
}

playerClick = (i) => {
    if (this.state.ships.includes(i)) {
        let hit = this.state.gameBoard
        hit[i] = "x"
        this.setState({
            gameBoard: hit,
        })
    }
}

